import jsonfrom flask import Flask, request, jsonifyimport consulapp = Flask(__name__)consul_client = consul.Consul()def get_namespace_config(namespace):    index, data = consul_client.kv.get(f'namespace/{namespace}')    if data:        return json.loads(data['Value'].decode('utf-8'))    return Nonedef set_namespace_config(namespace, config):    consul_client.kv.put(f'namespace/{namespace}', json.dumps(config))def validate_namespace_config(data):    required_keys = {"namespace", "relations"}    if not all(key in data for key in required_keys):        return False    relations = data["relations"]    if not isinstance(relations, dict):        return False    for relation, rules in relations.items():        if not isinstance(rules, dict):            return False        if "union" in rules:            if not isinstance(rules["union"], list):                return False            for rule in rules["union"]:                if not isinstance(rule, dict):                    return False                if "this" in rule and not isinstance(rule["this"], dict):                    return False                if "computed_userset" in rule:                    if not isinstance(rule["computed_userset"], dict):                        return False                    if "relation" not in rule["computed_userset"]:                        return False    return True@app.route('/namespace', methods=['POST'])def create_namespace():    print("AAAAAA")    data = request.json    if not validate_namespace_config(data):        return jsonify({"status": "error", "message": "Invalid namespace configuration"}), 400    namespace = data['namespace']    config = data['relations']    set_namespace_config(namespace, config)    return jsonify({"status": "success"}), 201if __name__ == '__main__':    app.run(debug=True)